// greetingHandler.js

const getGreetingMessage = () => {
    const pathname = window.location.href;
    const pathnameSegments = pathname.split('/');
    let greeting;
    let depan;
    let belakang;
    if (pathnameSegments[4] == null) {
        greeting = pathnameSegments[3].charAt(0).toUpperCase() + pathnameSegments[3].slice(1);
    } else {
        depan = pathnameSegments[3].charAt(0).toUpperCase() + pathnameSegments[3].slice(1);
        belakang = pathnameSegments[4].charAt(0).toUpperCase() + pathnameSegments[4].slice(1);
        greeting = depan + belakang;
    }
    return greeting;
    // return `ðŸ‘‹ Hello ${name}, ${greeting}!`;
};

export default getGreetingMessage;
