import React from 'react';
import PropTypes from 'prop-types'; // Import prop-types
import { NavLink, useLocation } from 'react-router-dom';
import menuData from '../data/menuData.json';

const MenuItem = ({ item }) => {
    const location = useLocation();
    const isActive = item.link && location.pathname === item.link;
    const hasSubmenu = item.submenu && item.submenu.length > 0;
    const isSubmenuActive = hasSubmenu && item.submenu.some(subitem => subitem.link && location.pathname === subitem.link);

    // Menampilkan pesan kesalahan jika properti 'link' tidak tersedia dalam objek 'item'
    if (!item.link) {
        return (
            <li className="menu-item">
                <span className="menu-link-error">Error: Link not available</span>
            </li>
        );
    }

    return (
        <li className={`menu-item ${isActive || isSubmenuActive ? 'active' : ''} ${hasSubmenu && isSubmenuActive ? 'open' : ''}`}>
            {item.link && (
                <NavLink
                    aria-label={`Navigate to ${item.text} ${!item.available ? 'Pro' : ''}`}
                    to={item.link}
                    className={`menu-link ${item.submenu ? 'menu-toggle' : ''}`}
                    target={item.link.includes('http') ? '_blank' : undefined}
                >
                    <i className={`menu-icon tf-icons ${item.icon}`}></i>
                    <div>{item.text}</div> {item.available === false && (
                        <div className="badge bg-label-primary fs-tiny rounded-pill ms-auto">Pro</div>
                    )}
                </NavLink>
            )}
            {item.submenu && (
                <ul className="menu-sub">{item.submenu.map((subitem, index) => (
                    <MenuItem key={index} item={subitem} />
                ))}</ul>
            )}
        </li>
    );
};


// Validasi prop-types
MenuItem.propTypes = {
    item: PropTypes.shape({
        link: PropTypes.string,
        submenu: PropTypes.arrayOf(PropTypes.object),
        icon: PropTypes.string,
        text: PropTypes.string,
        available: PropTypes.bool
    }).isRequired
};

export default MenuItem;
