import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

const DropzoneField = ({ fieldName, label, formData, setFormData }) => {
    const onDrop = (acceptedFiles) => {
        if (acceptedFiles && acceptedFiles.length > 0) {
            setFormData((prevFormData) => ({
                ...prevFormData,
                [fieldName]: acceptedFiles[0]
            }));
        } else {
            alert('Hanya file gambar yang diizinkan!');
        }
    };

    const { getRootProps, getInputProps } = useDropzone({
        onDrop,
        accept: 'image/jpeg, image/png', // Mengizinkan hanya file .jpeg dan .png
        maxFiles: 1
    });

    return (
        <div className="col-md-6 col-sm-12 mb-3">
            <label htmlFor={fieldName} className="form-label">{label}</label>
            <div {...getRootProps()} className="dropzone form-control">
                <input {...getInputProps()} />
                {!formData[fieldName] ? (
                    <p className='mt-3 text-center'>Pilih atau Drag n Drop {label.toLowerCase()}</p>
                ) : (
                    <div className='mt-3'>
                        <p>{formData[fieldName].name}</p>
                        <img
                            src={URL.createObjectURL(formData[fieldName])}
                            alt="Preview"
                            style={{ maxWidth: '100%', height: 'auto' }}
                        />
                    </div>
                )}
            </div>
        </div>
    );
};

DropzoneField.propTypes = {
    fieldName: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    formData: PropTypes.object.isRequired,
    setFormData: PropTypes.func.isRequired,
};

const AddPage = ({ currentView, handleBackClick }) => {
    const [formData, setFormData] = useState({
        foto_tampak_depan: null,
        foto_tampak_belakang: null,
        foto_tampak_kanan: null,
        foto_tampak_kiri: null,
        nopol: '',
        merk_mobil: '',
        jenis_tipe_mobil: '',
        model_mobil: '',
        warna_mobil: '',
        warna_tnkb_mobil: '',
        tahun_pembuatan_mobil: '',
        tahun_registrasi_mobil: '',
        ukuran_silinder_mobil: '',
        jenis_bahan_bakar_mobil: '',
        nomor_mesin_mobil: '',
        nomor_rangka_mobil: '',
        nomor_bpkb_mobil: '',
        nomor_stnk_mobil: '',
        masa_berlaku_stnk_mobil: '',
        bobot_mobil: '',
        nomor_kir_mobil: '',
        masa_berlaku_kir_mobil: '',
        status_mobil: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const data = new FormData();
            data.append('foto_tampak_depan', formData.foto_tampak_depan);
            data.append('foto_tampak_belakang', formData.foto_tampak_belakang);
            data.append('foto_tampak_kanan', formData.foto_tampak_kanan);
            data.append('foto_tampak_kiri', formData.foto_tampak_kiri);
            data.append('nopol', formData.nopol);
            data.append('merk_mobil', formData.merk_mobil);
            data.append('jenis_tipe_mobil', formData.jenis_tipe_mobil);
            data.append('model_mobil', formData.model_mobil);
            data.append('warna_mobil', formData.warna_mobil);
            data.append('warna_tnkb_mobil', formData.warna_tnkb_mobil);
            data.append('tahun_pembuatan_mobil', formData.tahun_pembuatan_mobil);
            data.append('tahun_registrasi_mobil', formData.tahun_registrasi_mobil);
            data.append('ukuran_silinder_mobil', formData.ukuran_silinder_mobil);
            data.append('jenis_bahan_bakar_mobil', formData.jenis_bahan_bakar_mobil);
            data.append('nomor_mesin_mobil', formData.nomor_mesin_mobil);
            data.append('nomor_rangka_mobil', formData.nomor_rangka_mobil);
            data.append('nomor_bpkb_mobil', formData.nomor_bpkb_mobil);
            data.append('nomor_stnk_mobil', formData.nomor_stnk_mobil);
            data.append('masa_berlaku_stnk_mobil', formData.masa_berlaku_stnk_mobil);
            data.append('bobot_mobil', formData.bobot_mobil);
            data.append('nomor_kir_mobil', formData.nomor_kir_mobil);
            data.append('masa_berlaku_kir_mobil', formData.masa_berlaku_kir_mobil);
            data.append('status_mobil', formData.status_mobil);

            const response = await axios.post('http://localhost:5050/api/mobil/add', data, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log('Response:', response.data);
            alert('Data mobil berhasil ditambahkan!');
            handleBackClick();
        } catch (error) {
            console.error('Error submitting form:', error);
            alert('Gagal menambahkan data mobil.');
        }
    };

    return (
        <div className="row">
            <div className="col-lg-12">
                <form onSubmit={handleSubmit}>
                    <div className="row">
                        <DropzoneField
                            fieldName="foto_tampak_depan"
                            label="Tampak Depan Mobil"
                            formData={formData}
                            setFormData={setFormData}
                        />
                        <DropzoneField
                            fieldName="foto_tampak_belakang"
                            label="Tampak Belakang Mobil"
                            formData={formData}
                            setFormData={setFormData}
                        />
                        <DropzoneField
                            fieldName="foto_tampak_kanan"
                            label="Tampak Kanan Mobil"
                            formData={formData}
                            setFormData={setFormData}
                        />
                        <DropzoneField
                            fieldName="foto_tampak_kiri"
                            label="Tampak Kiri Mobil"
                            formData={formData}
                            setFormData={setFormData}
                        />
                    </div>
                    <div className="row">
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="nopol" className="form-label">Nomor Polisi</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nopol"
                                name="nopol"
                                value={formData.nopol}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="merk_mobil" className="form-label">Merk Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="merk_mobil"
                                name="merk_mobil"
                                value={formData.merk_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="jenis_tipe_mobil" className="form-label">Jenis Tipe Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="jenis_tipe_mobil"
                                name="jenis_tipe_mobil"
                                value={formData.jenis_tipe_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="model_mobil" className="form-label">Model Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="model_mobil"
                                name="model_mobil"
                                value={formData.model_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="warna_mobil" className="form-label">Warna Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="warna_mobil"
                                name="warna_mobil"
                                value={formData.warna_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="warna_tnkb_mobil" className="form-label">Warna TNKB Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="warna_tnkb_mobil"
                                name="warna_tnkb_mobil"
                                value={formData.warna_tnkb_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="tahun_pembuatan_mobil" className="form-label">Tahun Pembuatan Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="tahun_pembuatan_mobil"
                                name="tahun_pembuatan_mobil"
                                value={formData.tahun_pembuatan_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="tahun_registrasi_mobil" className="form-label">Tahun Registrasi Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="tahun_registrasi_mobil"
                                name="tahun_registrasi_mobil"
                                value={formData.tahun_registrasi_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="ukuran_silinder_mobil" className="form-label">Ukuran Silinder Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="ukuran_silinder_mobil"
                                name="ukuran_silinder_mobil"
                                value={formData.ukuran_silinder_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="jenis_bahan_bakar_mobil" className="form-label">Jenis Bahan Bakar Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="jenis_bahan_bakar_mobil"
                                name="jenis_bahan_bakar_mobil"
                                value={formData.jenis_bahan_bakar_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="nomor_mesin_mobil" className="form-label">Nomor Mesin Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nomor_mesin_mobil"
                                name="nomor_mesin_mobil"
                                value={formData.nomor_mesin_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="nomor_rangka_mobil" className="form-label">Nomor Rangka Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nomor_rangka_mobil"
                                name="nomor_rangka_mobil"
                                value={formData.nomor_rangka_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="nomor_bpkb_mobil" className="form-label">Nomor BPKB Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nomor_bpkb_mobil"
                                name="nomor_bpkb_mobil"
                                value={formData.nomor_bpkb_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="nomor_stnk_mobil" className="form-label">Nomor STNK Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nomor_stnk_mobil"
                                name="nomor_stnk_mobil"
                                value={formData.nomor_stnk_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="masa_berlaku_stnk_mobil" className="form-label">Masa Berlaku STNK Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="masa_berlaku_stnk_mobil"
                                name="masa_berlaku_stnk_mobil"
                                value={formData.masa_berlaku_stnk_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="bobot_mobil" className="form-label">Bobot Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="bobot_mobil"
                                name="bobot_mobil"
                                value={formData.bobot_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="nomor_kir_mobil" className="form-label">Nomor KIR Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nomor_kir_mobil"
                                name="nomor_kir_mobil"
                                value={formData.nomor_kir_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="masa_berlaku_kir_mobil" className="form-label">Masa Berlaku KIR Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="masa_berlaku_kir_mobil"
                                name="masa_berlaku_kir_mobil"
                                value={formData.masa_berlaku_kir_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div className="col-md-6 col-sm-12 mb-3">
                            <label htmlFor="status_mobil" className="form-label">Status Mobil</label>
                            <input
                                type="text"
                                className="form-control"
                                id="status_mobil"
                                name="status_mobil"
                                value={formData.status_mobil}
                                onChange={handleChange}
                                required
                            />
                        </div>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>
    );
};

AddPage.propTypes = {
    currentView: PropTypes.string.isRequired,
    handleBackClick: PropTypes.func.isRequired
};

export default AddPage;
