// greetingHandler.js

const getGreetingMessage = () => {
    const pathname = window.location.href;
    const pathnameSegments = pathname.split('/');
    let greeting;
    if (pathnameSegments[4] == null) {
        let modifiedString = pathnameSegments[3].charAt(0).toUpperCase() + pathnameSegments[3].slice(1);
        greeting = pathnameSegments[3];
    } else {
        greeting = pathnameSegments[3];
    }
    return greeting;
    // return `ðŸ‘‹ Hello ${name}, ${greeting}!`;
};

export default getGreetingMessage;
